import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import './styles.css';

const socket = io('http://localhost:3000');

function App() {
  const [username, setUsername] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [hoveredMessage, setHoveredMessage] = useState(null);

  useEffect(() => {
    // Escuchar los mensajes de chat del servidor
    socket.on('chat_message', (data) => {
      setMessages((prevMessages) => [...prevMessages, data]);
    });

    return () => {
      socket.off('chat_message'); // Limpiar el evento
    };
  }, []);

  const handleLogin = () => {
    if (username.trim()) {
      socket.emit('set_username', username);
      setIsLoggedIn(true);
    }
  };

  const handleSendMessage = () => {
    if (message.trim()) {
      socket.emit('chat_message', { usuario: username, mensaje: message, tipo: 'texto' });
      setMessage('');
    }
  };

  const handleDeleteMessage = (index) => {
    setMessages((prevMessages) => prevMessages.filter((_, i) => i !== index));
  };

  const handleEditMessage = (index) => {
    const newMessage = prompt('Edita tu mensaje:', messages[index].mensaje);
    if (newMessage) {
      setMessages((prevMessages) =>
        prevMessages.map((msg, i) =>
          i === index ? { ...msg, mensaje: newMessage } : msg
        )
      );
    }
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        socket.emit('chat_message', { usuario: username, mensaje: reader.result, tipo: 'imagen' });
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className="container">
      {!isLoggedIn ? (
        <div>
          <h2>Ingresa tu nombre</h2>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Nombre de usuario"
          />
          <button onClick={handleLogin}>Entrar al chat</button>
        </div>
      ) : (
        <div>
          <h2>Chat</h2>
          <ul>
            {messages.map((msg, index) => (
              <li
                key={index}
                className={msg.usuario === username ? 'self' : ''}
                onMouseEnter={() => setHoveredMessage(index)}
                onMouseLeave={() => setHoveredMessage(null)}
              >
                <strong>{msg.usuario}: </strong>
                {msg.tipo === 'texto' ? (
                  msg.mensaje
                ) : (
                  <img src={msg.mensaje} alt="enviado" style={{ maxWidth: '200px' }} />
                )}
                {hoveredMessage === index && msg.usuario === username && (
                  <div className="actions">
                    <button onClick={() => handleEditMessage(index)}>Editar</button>
                    <button onClick={() => handleDeleteMessage(index)}>Eliminar</button>
                  </div>
                )}
              </li>
            ))}
          </ul>
          <input
            type="text"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            placeholder="Escribe un mensaje..."
          />
          <button onClick={handleSendMessage}>Enviar</button>
          <input type="file" accept="image/*" onChange={handleImageUpload} />
        </div>
      )}
    </div>
  );
}

export default App;
