// src/App.js
import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:3000');

function App() {
  const [username, setUsername] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    // Escuchar los mensajes de chat del servidor
    socket.on('chat_message', (data) => {
      setMessages((prevMessages) => [...prevMessages, data]);
    });

    return () => {
      // Limpiar el evento cuando se desmonte el componente
      socket.off('chat_message');
    };
  }, []);

  const handleLogin = () => {
    if (username.trim()) {
      socket.emit('set_username', username); // Enviar el nombre al servidor
      setIsLoggedIn(true); // Cambiar el estado a "logueado"
    }
  };

  const handleSendMessage = () => {
    if (message.trim()) {
      socket.emit('chat_message', { mensaje: message }); // Enviar el mensaje al servidor
      setMessage(''); // Limpiar el campo de texto
    }
  };

  return (
    <div>
      {!isLoggedIn ? (
        <div>
          <h2>Ingresa tu nombre</h2>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Nombre de usuario"
          />
          <button onClick={handleLogin}>Entrar al chat</button>
        </div>
      ) : (
        <div>
          <h2>Chat</h2>
          <ul>
            {messages.map((msg, index) => (
              <li key={index}>
                <strong>{msg.usuario}: </strong> {msg.mensaje}
              </li>
            ))}
          </ul>
          <input
            type="text"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            placeholder="Escribe un mensaje..."
          />
          <button onClick={handleSendMessage}>Enviar</button>
        </div>
      )}
    </div>
  );
}

export default App;
