import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import './styles.css';

const socket = io('http://localhost:3000');

function App() {
  const [username, setUsername] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [hoveredMessage, setHoveredMessage] = useState(null);

  useEffect(() => {
    // Escuchar mensajes nuevos del servidor
    socket.on('chat_message', (data) => {
      setMessages((prevMessages) => [...prevMessages, data]);
    });

    // Escuchar mensajes editados
    socket.on('edit_message', (data) => {
      setMessages((prevMessages) =>
        prevMessages.map((msg) =>
          msg.id === data.id ? { ...msg, mensaje: data.mensaje } : msg
        )
      );
    });

    return () => {
      socket.off('chat_message');
      socket.off('edit_message');
    };
  }, []);

  const handleLogin = () => {
    if (username.trim()) {
      socket.emit('set_username', username);
      setIsLoggedIn(true);
    }
  };

  const handleSendMessage = () => {
    if (message.trim()) {
      const newMessage = {
        id: Date.now(), // ID único basado en la hora
        usuario: username,
        mensaje: message,
        tipo: 'texto',
      };
      socket.emit('chat_message', newMessage);
      setMessage('');
    }
  };

  const handleEditMessage = (msg) => {
    const newMessage = prompt('Edita tu mensaje:', msg.mensaje);
    if (newMessage) {
      const editedMessage = { ...msg, mensaje: newMessage };
      socket.emit('edit_message', editedMessage); // Enviar al servidor
    }
  };

  const handleDeleteMessage = (msg) => {
    socket.emit('delete_message', msg.id); // Emitir evento de eliminación (opcional)
    setMessages((prevMessages) => prevMessages.filter((m) => m.id !== msg.id));
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        const newMessage = {
          id: Date.now(),
          usuario: username,
          mensaje: reader.result,
          tipo: 'imagen',
        };
        socket.emit('chat_message', newMessage);
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className="container">
      {!isLoggedIn ? (
        <div>
          <h2>Ingresa tu nombre</h2>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Nombre de usuario"
          />
          <button onClick={handleLogin}>Entrar al chat</button>
        </div>
      ) : (
        <div>
          <h2>Chat</h2>
          <ul>
            {messages.map((msg) => (
              <li
                key={msg.id}
                className={msg.usuario === username ? 'self' : ''}
                onMouseEnter={() => setHoveredMessage(msg.id)}
                onMouseLeave={() => setHoveredMessage(null)}
              >
                <strong>{msg.usuario}: </strong>
                {msg.tipo === 'texto' ? (
                  msg.mensaje
                ) : (
                  <img src={msg.mensaje} alt="enviado" style={{ maxWidth: '200px' }} />
                )}
                {hoveredMessage === msg.id && msg.usuario === username && (
                  <div className="actions">
                    <button onClick={() => handleEditMessage(msg)}>Editar</button>
                    <button onClick={() => handleDeleteMessage(msg)}>Eliminar</button>
                  </div>
                )}
              </li>
            ))}
          </ul>
          <input
            type="text"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            placeholder="Escribe un mensaje..."
          />
          <button onClick={handleSendMessage}>Enviar</button>
          <input type="file" accept="image/*" onChange={handleImageUpload} />
        </div>
      )}
    </div>
  );
}

export default App;
